$(document).ready(function(){createPopup();
$(".popup-close").click(function(){$(".popup-overlay").fadeOut()
});
checklocalStorage();
$(document).on("click",function(b){if($(b.target).parents(".popup-overlay").size()==0&&(!$(b.target).is(".popup-close"))){$(".popup-overlay").fadeOut()
}})
});
function checklocalStorage(){if(!fetch_fromLocalStorage("popup")){save_ToLocalStorage("popup","shown");
$(".popup-overlay").fadeIn()
}}function createPopup(){$("body").append('<div class="popup-overlay"><div class="popup-wrap" style="max-width:350px"><a href="#" class="popup-close"><img src="/content/dam/doitassets/common/images/popup/close.png"></a><div class="modal-popup"><img src="/content/dam/doitassets/common/images/popup/BhamashahAd.jpg"></div></div></div>')
}function save_ToLocalStorage(d,c){c=JSON.stringify(c);
localStorage.setItem(d,c)
}function fetch_fromLocalStorage(d){var c=localStorage.getItem(d);
return JSON.parse(c)
};